class Progress{constructor(){}initialize(){this.changeProgress(),this.inputHandler()}changeProgress(){let e=document.querySelector(".form-svg__input"),r=document.querySelector(".progress-indicator"),t=document.querySelector(".progress-bar"),s=t.getAttribute("r"),o=Math.PI*(2*s);e.addEventListener("input",()=>this.inputHandler(e,r,o,t))}inputHandler(e,r,t,s){setTimeout(()=>{e.value>-1&&e.value<101?console.log("works"):e.value="";let o=(100-e.value)/100*t;r.innerHTML=e.value+"%",s.setAttribute("stroke-dashoffset",o.toString())},600)}}const progressBar=new Progress;progressBar.initialize();