class FormValidator{constructor(e,t){this.form=e,this.fields=t}initialize(){this.validateOnSubmit(),this.validateOnEntry()}validateOnSubmit(){let e=this;this.form.addEventListener("submit",t=>{t.preventDefault(),e.fields.forEach(t=>{const l=document.querySelector(`#${t}`);e.validateFields(l)})})}validateOnEntry(){let e=this;this.fields.forEach(t=>{const l=document.querySelector(`#${t}`);l.addEventListener("input",t=>{e.validateFields(l)})})}validateFields(e){console.log(e.parentElement.nextSibling.innerHTML="it works!")}}const form=document.querySelector("#form"),fields=["name","password","countries"],validator=new FormValidator(form,fields);validator.initialize(),document.getElementById("eye").onclick=(()=>{document.getElementById("password").attributes.type.value="text",document.getElementById("eye").style.display="none",document.getElementById("eye-slashed").style.display="block"}),document.getElementById("eye-slashed").onclick=(()=>{document.getElementById("password").attributes.type.value="password",document.getElementById("eye-slashed").style.display="none",document.getElementById("eye").style.display="block"});