class Animation{constructor(t,e,n,d){this.modal=t,this.expandButton=e,this.declineButton=n,this.deleteButton=d}initialize(){this.expandModal(),this.closeModal()}expandModal(){this.expandButton.addEventListener("click",()=>{this.modal.style.display="flex",this.expandButton.style.display="none"})}closeModal(){this.declineButton.addEventListener("click",()=>{this.modal.style.display="none",this.expandButton.style.display="block"}),this.deleteButton.addEventListener("click",()=>{this.modal.style.display="none",this.expandButton.style.display="block"})}}let modal=document.getElementById("expand-modal"),expandButton=document.getElementById("expand-button"),declineButton=document.getElementById("expand-button-decline"),deleteButton=document.getElementById("expand-button-delete");const animate=new Animation(modal,expandButton,declineButton,deleteButton);animate.initialize();